mapper.select:
SELECT article_id,article_content,`options`,user_name,user_type,user_img,college_name,article_message.create_time FROM article_message LEFT JOIN `user` ON article_message.user_id = `user`.id LEFT JOIN college ON article_message.college_id = college.id where article_message.user_id = `user`.id
article_message.article_id = #{articleId}
and article_message.user_id = #{userId}
and article_message.college_id = #{collegeId}
ORDER BY article_message.create_time DESC
